    <script type="text/javascript">
        $(document).ready(function () {
            $('.block .title').click(function () {
                var e = window, a = 'inner';
                if (!('innerWidth' in window)) {
                    a = 'client';
                    e = document.documentElement || document.body;
                }
                var result = { width: e[a + 'Width'], height: e[a + 'Height'] };
                if (result.width < 1001) {
                    $(this).siblings('.listbox').slideToggle('slow');
                }
            });

            $('.footer-block .title').click(function () {
                $(this).toggleClass('expanded');
                var e = window, a = 'inner';
                if (!('innerWidth' in window)) {
                    a = 'client';
                    e = document.documentElement || document.body;
                }
                var result = { width: e[a + 'Width'], height: e[a + 'Height'] };
                if (result.width < 769) {
                    $(this).siblings('.list').slideToggle('slow');
                }
            });

            $('.menu-toggle').click(function () {
                //$(this).siblings('.top-menu.mobile').slideToggle('slow');
                $(this).toggleClass('cross');
                $('body').toggleClass('no-scroll');
                $('.top-menu.mobile').toggleClass('show');
                $('#mobileMenuOverlay').toggleClass('show');
                $('ul.ulCog').removeClass('show');
            });
            $('#mobileMenuOverlay').click(function () {
                $('body').toggleClass('no-scroll');
                $('.top-menu.mobile').toggleClass('show');
                $('#mobileMenuOverlay').toggleClass('show');
                $('div.menu-toggle').toggleClass('cross');
            });

            $('.top-menu.mobile .sublist-toggle').click(function () {
                $(this).toggleClass('expand');
                $(this).siblings('.sublist').slideToggle('slow');
            });
            $('#mobileCog').click(function () {
                $(this).siblings('ul.ulCog').toggleClass('show');
                $('div.menu-toggle').removeClass('cross');
                $('body').removeClass('no-scroll');
                $('.top-menu.mobile').removeClass('show');
                $('#mobileMenuOverlay').removeClass('show');
            });

            $('#small-searchterms').autocomplete({
                delay: 500,
                minLength: 1,
                source: '/catalog/searchtermautocomplete',
                appendTo: '.search-box',
                select: function (event, ui) {
                    $("#small-searchterms").val(ui.item.label);
                    setLocation(ui.item.producturl);
                    return false;
                }
            }).data("ui-autocomplete")._renderItem = function (ul, item) {
                    var t = item.label;
                    //html encode
                    t = htmlEncode(t);
                    return $("<li></li>")
                        .data("item.autocomplete", item)
                        .append("<a><img src='" + item.productpictureurl + "'><span>" + t + "</span></a>")
                        .appendTo(ul);
                };


            $("#small-search-box-form").submit(function (event) {
                if ($("#small-searchterms").val() == "") {
                    alert('Please enter some search keyword');
                    $("#small-searchterms").focus();
                    event.preventDefault();
                }
            });

            
            $('.home-page-top-slider').slick({
                dots: true,
                arrows: false,
                infinite: true,
                speed: 500,
                fade: true,
                cssEase: 'linear'
            });


            // On click buy-now-action button open modal
            $('.buy-now-action').click(function () {
                $('#orderForm').hide();
                $('#dynamic_content').hide();
                $('#orderPlacedMessage').hide();
                $('#orderAlert').hide();
                $('.btn-modal-close').html('{{ __("cancel") }}');
                $('.btn-order-submit').html('{{ __("order_now") }}').show();
                $('#buyNowModal').modal('show');
                $('#checkout_loading').fadeIn();
                // ajax request to get product details
                $.ajax({
                    url: "{{ route('checkout', '') }}" + '/' + $(this).data('product-id'),
                    type: 'POST',
                    data: {
                        product_id: $(this).data('product-id')
                    },
                    success: function (response) {
                        $('#checkout_loading').hide();
                        $('#dynamic_content').html(response).fadeIn();
                        $('#orderForm').fadeIn();
                        $('.btn-order-submit').removeAttr('disabled');
                    },
                    error: function (error) {
                        $('#checkout_loading').hide();
                        $('#dynamic_content').html('<h3 class="text-center">{{ __("sorry_something_went_wrong") }}</h3>').fadeIn();
                        $('.btn-order-submit').removeAttr('disabled');
                    }
                });
            });

            // On click order-submit button
            $('.btn-order-submit').click(function (){
                $(this).attr('disabled', true).html('{{ __("order_processing") }}...');
                $('#orderAlert').fadeOut();
                // ajax request to place order
                $.ajax({
                    url: "{{ route('place-order') }}",
                    type: 'POST',
                    data: {
                        product_id: $('#product_id').val(),
                        item_quantity: $('.qty-input').val(),
                        customer_name: $('#customerName').val(),
                        customer_phone: $('#phnno').val(),
                        customer_address: $('#address').val()
                    },
                    success: function (response) {
                        if(response.status == 'success'){
                            $('#dynamic_content').html('');
                            $('#orderForm').hide();
                            $('.btn-order-submit').hide();
                            $('.btn-modal-close').html('{{ __("okay") }}');
                            $('#orderPlacedMessage').fadeIn();
                            $('#orderSuccessID').text(response.order_id);
                        }else{
                            $('#orderAlertText').text(response.message);
                            $('#orderAlert').fadeIn();
                            $('.btn-order-submit').html('{{ __("order_now") }}').removeAttr('disabled');
                        }
                    },
                    error: function (error) {
                        $('#orderAlertText').text('{{ __("sorry_something_went_wrong") }}');
                        $('#orderAlert').fadeIn();
                        $('.btn-order-submit').html('{{ __("order_now") }}').removeAttr('disabled');
                    }
                });
            });


            $(window).scroll(function () {
                if ($(this).scrollTop() > 100) {
                    $('#scroll').fadeIn();
                } else {
                    $('#scroll').fadeOut();
                }
            });
            $('#scroll').click(function () {
                $("html, body").animate({ scrollTop: 0 }, 600);
                return false;
            });
            var windowWidth = $(window).width();
            if (windowWidth > 1000) {
                $('body').css('padding-bottom', $('div.footer').innerHeight() + 10);
            }
        });
    </script>

